$(document).ready(function() {
	$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table').addClass("table");
    
		$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table').addClass("table-striped");
	
	$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table').addClass("table-borderless");
	
		var removeHeading = false;
		
		$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table tbody tr').eq(0).find("td").replaceWith(function(){
			return $("<th>", {attr: {'width': $(this).attr('width'), 'rowspan': $(this).attr('rowspan'), 'colspan': $(this).attr('colspan'), 'valign': $(this).attr('valign'), 'headers': $(this).attr('headers'), 'style': $(this).attr('style')}, html: $(this).html()}).css("vertical-align", $(this).attr('valign'));;
		});
		$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table:not(.dataTable) tbody tr').eq(0).wrap( "<thead></thead>" );
	
		$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table thead').prependTo('#aec045e9-d4fa-403c-bbfd-4abd38017669 table');
		
		
		$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table').removeAttr("border");
		$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table').removeAttr("cellpadding");
		$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table').removeAttr("cellspacing");
		
		$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table').DataTable({
			
			"sDom":"lrtp",
			"sort":false,
			"pagingType": "simple_numbers","paging":false,
			"autoWidth":true
		});
	
		//#NOTE-REMOVE
		if(removeHeading){
			$('#aec045e9-d4fa-403c-bbfd-4abd38017669 table thead').remove();
		}
		
		
	    
    
    
} );


