.search_form .search_input, .search_form input[type="search"]  {
	background-color: #ffffff;
}

#main>.search_form .search_input, .search_form input[type="search"] {background-color: #e5e5e5;}

/* Style for Course Readings Images */
.coursereadingimage {
	margin-left: 2em;
	filter: drop-shadow(5px 5px 5px grey);
	margin-top: 0.5em;
}

/* Extra padding after li entries in ol and ul lists */
.padded-list li{
	padding-bottom: 1em;
}

/* Black Heritage Exhibit Page */
.narrow {
	float: left;
}
.wide {
	float: right;
}
.bottomborder {
	float: left;
	padding-bottom: 1em;
}

/* Additional padding at top of image */
.topspace20 {
	margin-top: 20px;
}
/*  TEMPORARY 1.27.0 hotfix */
.widget.section-widget .menu-item a {
  background: var(--white);
  color: var(--black);
  text-decoration: underline;
  padding: 10px 15px;
}
.widget.section-widget .menu-item a:hover {
  background: var(--black);
  color: var(--white);
}
/* Fix Textbook search box */
.textbook_search,.search_input {
	width: 80%;
}
/* Remove underline from h2 a links */
h2 a {
	text-decoration: none;
}
h2 a:hover {
	text-decoration: underline;
}
/* Add some spacing between list items in columns */
div.wp-block-column li {
	padding-bottom: 0.5em;
}
ul.extra-spacing li {
	padding-bottom: 0.5em;
}
/* Styling for Staff Directory */
img.staff-member-photo {
	display: none;
}
div.staff-member-listing h3.staff-member-name {
	display: inline;
	padding-right: 1em;
	font-size: 1.0em
}
div.staff-member-listing h4.staff-member-position {
	display: inline;
	padding-right: 1em;
	font-size: 1em
}
a.staff-member-email {
	display: inline;
}
div.staff-member-bio p {
	display:inline;
}
div.bio-projects {
  display: none;
}
/* Fix size of heading on posts pages */
.post .entry-title {
	font-size: 1.75em;
}
.post h2 {
	font-size: 1.5em;
}
.post h3 {
	font-size: 1.3em;
}
.post h4 {
	font-size: 1.2em;
}
.post h5 {
	font-size: 1.1em;
}
.post h6 {
	font-size: 1em;
}
/* Spacing of lists */
ul.inc-height li {
	padding-bottom: 1em;
}
/* Customize Pop-up Chat Widget */
.ui-widget-content {
	font-weight: bold;
	border: 0;
	background-color: #ffffff !important;
}
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default
{background: #0077c3 !important;}

.ui-widget-header {
	border: 0;
	background: #ffdc00 !important;
}
.ui-dialog .ui-dialog-buttonpane button {
	padding: 1em;
	margin: 0.5em 0.4em 0.5em 1em;
}
.ui-dialog .ui-dialog-buttonpane .ui-dialog-buttonset {
	float: none !important;
	text-align: center;
}
#ui-id-2 {
	color: #000;
}
.ui-dialog.ui-dialog-content {
	padding-bottom: 2em;
}
/* Dynamic Ask Chat icon */
.libraryh3lp {
	background-color: rgb(235,235,235);
    padding: 1em 1em 0 1em;
    /* padding-top: 1em; */
    width: 150px;
}
div.needs-js {
	background-color: red;
}

/* Customized COVID messge on Service pages */
.contentWrapper {
	font-style: italic;
	background-color: #ffffff;
	padding: 1em 1em 1em 2em;
	border: 2px solid #ffdc00;
	display: flex;
  align-items: center;
}
.iconContainer:before {
	content: "\f06a";
	font-family: FontAwesome;
	padding-right: 1em;
	font-style: normal;
	font-size: 2.5em;
}
.textContainer a {
	font-style: normal;
}
/* Drop Shadow for images */
.dropshadow {
	 box-shadow: 9px 9px 6px 1px rgba(42,41,44,0.16);
    -webkit-box-shadow: 9px 9px 6px 1px rgba(42,41,44,0.16);
    -moz-box-shadow: 9px 9px 6px 1px rgba(42,41,44,0.16);
}
/* Fix for background color in even numbered rows in tables so that watermark does not show */
.table-striped>tbody>tr:nth-of-type(even) {
    background-color: var(--main-bg-color);
}

/* Quick things for Fall 2020 Homepage */
.rylib-homepage-bento .wp-block-column {
	padding: 20px;
	background: #fff;
	border-bottom: 10px #4CA6E8 solid;
}
.rylib-homepage-bento h2 {
	font-size: 2rem;
	margin-top: 0;
}
.rylib-homepage-bento ul {
	list-style: none;
	padding: 0px;
}

.rylib-homepage-bento a {
	color: #000;
}

.rylib-homepage-news h2, .rylib-homepage-workshops h2 {
	color: #0F306B;
	text-align: center;
	font-size: 2rem;
	margin-top: 0;
}

.rylib-homepage-workshops a, .rylib-homepage-workshops a:visited { color: #000 }
.rylib-homepage-workshops ul {
	padding: 0;
	list-style: none;
}
div.rylib-homepage-workshops li {
	padding-bottom: 1em;
}
.rylib-homepage-workshops .event { 	
	display: flex;
	font-weight: 700;
	color: #0F306B;
}

.rylib-homepage-workshops .event .datewrap {
	text-align: right;
	min-width: 110px;
}

.rylib-homepage-workshops .event .date {
	font-size: 1.25em;
}

.rylib-homepage-workshops .event .time {
    line-height: 1.3em;
    font-size: .9em;
    margin-top: 5px;
}

.rylib-homepage-workshops .event .description {
	color: #FFF;
	background: #0F306B;
	padding: 1em;
	margin-left: 1em;
	flex: 1 0 0;
}

.rylib-homepage-workshops .event .description a {
	color: #FFF;
	text-decoration: none;
}

.rylib-homepage-workshops .event .description a:hover {
	text-decoration: underline;
}

.rylib-land-ack {
	background: #FFF;
  padding: 1.5em;
  margin-bottom: 2em;
}

.rylib-land-ack p {
	margin: 0;
}

.rylib-land-ack .wp-block-buttons {
	position: absolute;
	right: 1.5em;
}

.rylib-land-ack .wp-block-button .wp-block-button__link {
	background: #4EA5E8;
	border-radius: 0;
}

/*Figshare test */
.search-form .sf-input input
{
    color: #252525;
    font-size: 16px;
    line-height: 30px;
    text-overflow: ellipsis;
    white-space: nowrap;
    height: 36px;
    width: 100%;
    max-width: 400px;
    padding: 0 36px 0 12px;
    border: 1px solid #ddd;
    box-shadow: none;
    border-radius: 3px;
    overflow: hidden;
}
.browse-menu {
	list-style: none;
	padding-left: 0;
	display: flex;
	flex-wrap: wrap;
}
.browse-menu li {
	padding: 3em 1.5em;
	background-color: white;
	margin-right: 2em;
	flex: 1 0 200px;
	width: 0;
	border: 1px solid #eaeaea;
	text-transform: uppercase;
	border-bottom: 10px #4ca6e8 solid;
		margin-top: 2em;
}
.search-form .sf-submit {
    position: absolute;
    top: 0;
    right: 0;
    width: 36px;
    height: 36px;
    padding: 0;
    font-size: 14px;
    color: #bbb;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-justify-content: center;
    -ms-flex-pack: center;
    justify-content: center;
    background: none;
    cursor: pointer;
    border: none;
    border-radius: 4px;

}
form.dh-search-form input[type="search"]{
	background-color: #fff;
}
.text-input-component-wrapper {
	text-align: center;
  padding: 1em;
}

.home h2 a {
	color: #000;
  text-decoration: none;	
}
.home h2 a:hover {
	text-decoration: underline;
}

.rylib-tabs {
	text-align: left;
}
